import React from 'react';

// Storybook

// Components
import { Paragraph, Text, Title, H1, H2, H3, H4, H5, H6 } from './Typography';
import { StoryArticle, StoryHeader, StorySection } from '../../../utils/storybook/StyledStoryComponents';
import Docs from './TYPOGRAPHY.md';

// Utils
import { createStory } from '../../../utils/storybook/storybookUtils';

createStory('General|/').add('Typography', () => {
  const [editableString, updateEditableString] = React.useState('This is an editable text.')

  const onEditableChange = str => {
    updateEditableString(str);
  };

  return (
    <StoryArticle>
      <StoryHeader>
        <h1>Typography</h1>
      </StoryHeader>

      <StorySection>
        <H2>Title</H2>

        <Title>h1. Armory</Title>
        <H1>h1. Armory</H1>

        <Title level={2}>h2. Armory</Title>
        <H2>h2. Armory</H2>

        <Title level={3}>h3. Armory</Title>
        <H3>h3. Armory</H3>

        <Title level={4}>h4. Armory</Title>
        <H4>h4. Armory</H4>

        <Title level={5}>h5. Armory</Title>
        <H5>h5. Armory</H5>

        <Title level={6}>h6. Armory</Title>
        <H6>h6. Armory</H6>
      </StorySection>

      <StorySection>
        <H2>Text</H2>

        <Text>Ant Design</Text>
        <br />
        <Text type="secondary">Ant Design</Text>
        <br />
        <Text type="warning">Ant Design</Text>
        <br />
        <Text type="danger">Ant Design</Text>
        <br />
        <Text disabled>Ant Design</Text>
        <br />
        <Text mark>Ant Design</Text>
        <br />
        <Text code>Ant Design</Text>
        <br />
        <Text underline>Ant Design</Text>
        <br />
        <Text delete>Ant Design</Text>
        <br />
        <Text strong>Ant Design</Text>
      </StorySection>

      <StorySection>
        <H2>Paragraph</H2>

        <Paragraph editable={{ onChange: onEditableChange }}>{editableString}</Paragraph>
        <Paragraph copyable>This is a copyable text.</Paragraph>
        <Paragraph copyable={{ text: 'Hello, Ant Design!' }}>Replace copy text.</Paragraph>
      </StorySection>
    </StoryArticle>
  );
},
{
  notes: {
    markdown: Docs,
  },
});
